package practice;

public class Whileloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
           int n=20;
           int i=1;
           int sum=0;
           while(n>i) {
        	    
        	  sum=sum+i; 
        	  i++;
        	  }
           System.out.println(sum);
        	   
        	    
        	   }
	}



